import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Card, Row, Col, Container, Form } from "react-bootstrap";
import "./HealthBlog.css";

const HealthBlog = () => {
  const [blogs, setBlogs] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch("http://localhost:3001/healthblogs")
      .then((res) => res.json())
      .then((data) => setBlogs(data));
  }, []);

  const filteredBlogs = blogs.filter(
    (blog) =>
      blog.title.toLowerCase().includes(search.toLowerCase()) ||
      blog.summary.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container className="health-blog py-4">
      <h2 className="text-center mb-4">Health Blog</h2>
      <Form.Control
        type="text"
        placeholder="Search blog posts..."
        className="mb-4 search-input"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <Row className="gy-4">
        {filteredBlogs.map((blog) => (
          <Col md={6} lg={4} key={blog.id}>
            <Card className="blog-card h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={blog.thumbnail}
                className="card-img-top rounded-top"
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title className="blog-title mb-2">
                  {blog.title}
                </Card.Title>
                <Card.Text className="blog-summary flex-grow-1">
                  {blog.summary}
                </Card.Text>
                <Link to={`/healthblog/${blog.id}`} className="mt-3 blog-link">
                  Read more â†’
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default HealthBlog;
