import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";

const AppointmentForm = ({ doctor, onSubmit, onClose }) => {
  const [patientName, setPatientName] = useState("");
  const [email, setEmail] = useState("");
  const [timeslot, setTimeslot] = useState("");
  const [note, setNote] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!patientName || !email || !timeslot) {
      alert("Please fill in all required fields.");
      return;
    }

    const newAppointment = {
      id: uuidv4(),
      doctorId: doctor.id,
      doctorName: doctor.name,
      patientName,
      email,
      timeslot,
      note,
      status: "booked",
      createdAt: new Date().toISOString(),
    };

    onSubmit(newAppointment);
    onClose();
  };

  return (
    <div className="appointment-form p-4">
      <h5 className="mb-3">Book Appointment with Dr. {doctor.name}</h5>
      <form onSubmit={handleSubmit}>
        <div className="mb-2">
          <label className="form-label">Your Name *</label>
          <input
            type="text"
            className="form-control"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            required
          />
        </div>
        <div className="mb-2">
          <label className="form-label">Email *</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-2">
          <label className="form-label">Timeslot *</label>
          <input
            type="datetime-local"
            className="form-control"
            value={timeslot}
            onChange={(e) => setTimeslot(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Note (optional)</label>
          <textarea
            className="form-control"
            rows={2}
            value={note}
            onChange={(e) => setNote(e.target.value)}
          ></textarea>
        </div>
        <div className="d-flex justify-content-end">
          <button
            type="button"
            className="btn btn-secondary me-2"
            onClick={onClose}
          >
            Cancel
          </button>
          <button type="submit" className="btn btn-primary">
            Book
          </button>
        </div>
      </form>
    </div>
  );
};

export default AppointmentForm;
