import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Card, Row, Col, Container, Form } from "react-bootstrap";
import "./HealthBlog.css";

const HealthBlog = () => {
  const [blogs, setBlogs] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch("http://localhost:3001/healthblogs")
      .then((res) => res.json())
      .then((data) => setBlogs(data));
  }, []);

  const filteredBlogs = blogs.filter(
    (blog) =>
      blog.title.toLowerCase().includes(search.toLowerCase()) ||
      blog.summary.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container className="health-blog py-4">
      <h2 className="text-center mb-4">Health Blog</h2>
      <Form.Control
        type="text"
        placeholder="Search blog posts..."
        className="mb-4"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <Row>
        {filteredBlogs.map((blog) => (
          <Col md={6} lg={4} key={blog.id} className="mb-4">
            <Card className="h-100">
              <Card.Img variant="top" src={blog.thumbnail} />
              <Card.Body>
                <Card.Title>{blog.title}</Card.Title>
                <Card.Text>{blog.summary}</Card.Text>
                <Link to={`/healthblog/${blog.id}`}>Read more â†’</Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default HealthBlog;
